//------------------------------------------------------------------------------------------------------
//  ■ 用于实现场景对象的实时编辑器预览效果
//    -- 该代码为私有域，可任意增加变量，该变量作用域仅供此处使用
//    -- 该代码执行在编辑器中，所以一些游戏运行时独有的函数/类可能无法使用
//    -- 一旦编写了注册相关的逻辑，需要在onRemoved时取消该注册（如setInterval/os.add_ENTERFRAME等）
//       如果该模块已附加在了实体对象上，那么请在编写取消注册的逻辑后再保存或确定，否则可能造成泄漏
//       如果发生不可逆的泄漏，已经影响到了显示和操作，只能在编写正确的代码后通过重启编辑器解决。
//------------------------------------------------------------------------------------------------------
/**
 * 当前模块
 */
declare var soModule: SceneObjectModule_6;
/**
 * 当前模块的拥有者
 */
declare var owner: ProjectClientSceneObject;
/**
 * 打开对象操作工具（一般在选中时打开）
 * @param sp 目标对象
 */
declare var openOperateObjectTool: (sp: Sprite) => void;
/**
 * 刷新对象操作工具
 */
declare var refreshOperateObjectTool: () => void;
/**
 * 移移除对象操作工具
 */
declare var disposeOperateObjectTool: () => void;
/**
 * 刷新对象模块组件属性
 * @param varName 变量名
 * @param value 更改后的值
 */
declare var updateComp: (varName: string, value: any) => void;
/**
 * 编辑器预览接口
 */
declare var editor: Editor;

/**
 * 范围区域
 */
let rangeArea: UIBitmap;
let oldLayerLevel: number;
let oldVisible: boolean;
/**
 * 当前模块初始化（当添加该模块时）
 */
function init(): void {
    if (!rangeArea) {
        rangeArea = new UIBitmap();
        rangeArea.graphics.clear();
        rangeArea.width = soModule.width;
        rangeArea.height = soModule.height;
        oldVisible = owner.avatarContainer.visible;
        owner.avatarContainer.visible = !soModule.showRange || soModule.type == 2;
        oldLayerLevel = owner.layerLevel;
        owner.layerLevel = 2;
    }
    owner.animationLowLayer.addChild(rangeArea);
    updateRangeArea();
}
/**
 * 当移除该模块时
 * 一些自己增加的逻辑如需销毁，必须在此函数中实现
 */
function onRemoved(): void {
    if (rangeArea) rangeArea.removeSelf();
    owner.layerLevel = oldLayerLevel;
    owner.avatarContainer.visible = oldVisible;
    // 自定义形状
    onRemoved_customShapeSystem();
}
/**
 * 当改变当前模块属性时
 * @param attrName 模块的属性名称
 */
function onChange(attrName: string): void {
    if ([`width`, `height`, `radius`, `type`, `showRange`, `color`, `gridNum`].indexOf(attrName) != -1) {
        if (soModule.type == 1) {
            let w = soModule.radius * 2;
            let h = soModule.radius * 2;
            soModule.width = w;
            soModule.height = h;
            updateComp("width", w);
            updateComp("height", h);
        }
        rangeArea.width = soModule.width;
        rangeArea.height = soModule.height;
        updateRangeArea();
        refreshOperateObjectTool();
    }
    if (attrName == "type") {
        refreshOpenOperateObjectToolShow();
    }
    // 自定义形状
    onChange_customShape(attrName);
}
/**
 * 当前模块每帧执行的函数
 */
function onUpdate(): void {

}
/**
 * 当选中拥有者时的函数
 * @param isSingleSelect 是否单选
 */
function onSelectedOwner(isSingleSelect: boolean): void {
    updateRangeArea();
    if (soModule.type <= 1) openOperateObjectTool(rangeArea);
    Callback.CallLaterBeforeRender(() => {
        owner.layerLevel = 2;
    }, this);
    // 自定义形状
    onSelectedOwner_customShape(isSingleSelect);
}
/**
 * 当取消选中拥有者时的函数
 */
function onCancelSelectedOwner(): void {
    // 自定义形状
    onCancelSelectedOwner_customShape();
}
/**
 * 当拥有者的一般属性变更时（包含公共模块的属性变更）
 * @param attrName 拥有者的属性名称
 */
function onOwnerAttributeChange(attrName: string): void {
    if (attrName == "name") {
        updateRangeArea();
    }
    // 自定义形状
    onOwnerAttributeChange_customShape(attrName);
}
/**
 * 当拥有者的模块顺序发生改变时
 * @param fromIndex 模块原位置
 * @param toIndex 模块新位置
 */
function onChangeModulePostion(fromIndex: number, toIndex: number): void {

}
/**
 * 当对象操作工具开始操作时
 */
function onOperateObjectToolStart() {

}
/**
 * 当对象操作工具更改宽高时
 */
function onOperateObjectToolChangeWH() {
    soModule.width = rangeArea.width;
    soModule.height = rangeArea.height;
    let radius = Math.floor(Math.min(rangeArea.width / 2, rangeArea.height / 2));
    updateComp("width", soModule.width);
    updateComp("height", soModule.height);
    updateComp("radius", radius)
    updateRangeArea();
}
/**
 * 当对象操作工具操作结束时
 */
function onOperateObjectToolEnd() {

}
//------------------------------------------------------------------------------------------------------
//  刷新
//------------------------------------------------------------------------------------------------------
/**
 * 刷新显示区域
 */
function updateRangeArea() {
    if (!rangeArea) return;
    rangeArea.graphics.clear();
    if (!soModule.showRange) return;
    let w = soModule.type == 0 ? soModule.width : soModule.radius;
    let h = soModule.type == 0 ? soModule.height : soModule.radius;
    let myRectX = 0;//Math.floor(-w / 2 + 0); // soModule.offsetX
    let myRectY = 0;//Math.floor(-h / 2 + 0); // soModule.offsetY
    if (soModule.type != 2) {
        rangeArea.alpha = 0.5;
        owner.animationLowLayer.addChild(rangeArea);
    }
    else if (soModule.type == 2) {
        rangeArea.alpha = 1;
        owner.customHighLayer.addChild(rangeArea);
    }
    if (soModule.type == 0) {
        rangeArea.graphics.drawRect(myRectX, myRectY, w, h, soModule.color);
        rangeArea.graphics.fillText(`★${owner.name}`, 10, 10, `24px Arial`, "#000000", `left`);
    }
    else if (soModule.type == 1) {
        let x = soModule.width / 2;
        let y = soModule.height / 2;
        rangeArea.graphics.drawCircle(x, y, w, soModule.color);
        rangeArea.graphics.fillText(`★${owner.name}★`, x, y - 12, `24px Arial`, "#000000", `center`);
    }
    else if (soModule.type == 2) {
        rangeArea.graphics.fillText(`「${soModule.gridNum.toString()}」`, 0, -20, `40px Arial`, soModule.color, `center`);
    }
    else if (soModule.type == 3) {
        updateRangeArea_customShapeSystem();
    }
}
/**
 * 刷新操作工具是否显示
 */
function refreshOpenOperateObjectToolShow() {
    disposeOperateObjectTool();
    owner.avatarContainer.visible = !soModule.showRange || soModule.type == 2;
    if (soModule.type <= 1) openOperateObjectTool(rangeArea);
}
//------------------------------------------------------------------------------------------------------
//  自定义形状
//------------------------------------------------------------------------------------------------------
let cs_pointImgs: UIBitmap[] = [];
let cs_drawRoot: UIRoot = new UIRoot();
let cs_addImg: UIButton = new UIButton();
let cs_shiftKeyDown: boolean;
let cs_lockMoveOri: number; //0-无 1-x 2-y
let cs_lockMoveSpoint: Point[] = [];
let cs_currentPointIndex: number = -1;
let cs_recordLength: number = 0;
let cs_viewScale: number = 1;
let cs_isInit: boolean = false;
let cs_scaleChangeEvent: Callback;
let cs_savePresetStart: Callback;
let cs_savePresetEnd: Callback;
let cs_isRelativePoint = true;
/**
 * 初始化
 */
function initMove() {
    cs_isInit = true;
    cs_drawRoot = new UIRoot();
    owner.root.addChild(cs_drawRoot);
    cs_drawRoot.mouseEnabled = false;
    cs_addImg = new UIButton();
    cs_addImg.width = 18;
    cs_addImg.height = 18;
    cs_addImg.pivotX = 9;
    cs_addImg.pivotY = 9;
    cs_addImg.image1 = "editorAsset/img/plus_1.png";
    cs_addImg.image2 = "editorAsset/img/plus_2.png";
    cs_addImg.image3 = "editorAsset/img/plus_2.png";
    cs_addImg.on(EventObject.MOUSE_DOWN, this, onAddClick);
    cs_viewScale = editor.scene.sceneUIScale;
    cs_scaleChangeEvent = Callback.New(() => {
        cs_viewScale = editor.scene.sceneUIScale;
        let pointindex = cs_currentPointIndex;
        if (editor.scene.currentSceneObject == owner) {
            editor.scene.refreshSelectSceneObject();
            cs_currentPointIndex = pointindex;
            if (cs_currentPointIndex != -1 && cs_pointImgs[cs_currentPointIndex]) pointType(cs_pointImgs[cs_currentPointIndex], 1);
        }
    }, this);
    cs_savePresetStart = Callback.New(() => {
        cs_drawRoot.graphics.clear();
        removeMovePointImg();
    }, this);
    cs_savePresetEnd = Callback.New(() => {
        showMovePointImg();
    }, this);
    removeMovePointImg();
}
/**
 * 显示轨迹点图片
 */
function showMovePointImg() {
    let points = soModule.pointArr;
    cs_drawRoot.graphics.clear();
    if (!points || points.length == 0) removeMovePointImg();
    owner.root.addChild(cs_addImg);
    //刷新加号图片
    cs_addImg.scaleX = cs_addImg.scaleY = 1 / cs_viewScale;
    if (!points || points.length == 0) {
        cs_drawRoot.graphics.drawCircle(0, 0, 8 / cs_viewScale, "#fec11e", "#ffffff", 2)
        cs_addImg.pos(0, (15 + (cs_addImg.pivotY)) / cs_viewScale);
        return;
    }
    for (let i = 0; i < points.length; i++) {
        if (!cs_pointImgs[i]) {
            cs_pointImgs[i] = new UIBitmap();
            cs_pointImgs[i].width = 18;
            cs_pointImgs[i].height = 18;
            cs_pointImgs[i].pivotX = cs_pointImgs[i].pivotY = 9;
            pointType(cs_pointImgs[i], 0);
            owner.root.addChild(cs_pointImgs[i]);
            cs_pointImgs[i].on(EventObject.MOUSE_DOWN, this, onPointMouseDown, [i]);
            cs_pointImgs[i].on(EventObject.MOUSE_OVER, this, onPointMouseOver, [i]);
            cs_pointImgs[i].on(EventObject.MOUSE_OUT, this, onPointMouseOut, [i]);
            cs_pointImgs[i].on(EventObject.MOUSE_UP, this, onPointMouseUp);
            cs_pointImgs[i].on(EventObject.DRAG_START, this, onPointDragStart);
            cs_pointImgs[i].on(EventObject.DRAG_MOVE, this, onPointDragMove, [i]);
            cs_pointImgs[i].on(EventObject.DRAG_END, this, onPointMouseUp);
        }
        cs_pointImgs[i].scaleX = cs_pointImgs[i].scaleY = 1 / cs_viewScale;
        cs_pointImgs[i].x = cs_isRelativePoint ? points[i].x : points[i].x - owner.x;
        cs_pointImgs[i].y = cs_isRelativePoint ? points[i].y : points[i].y - owner.y;
        //划线
        if (i == 0) {
            cs_drawRoot.graphics.drawCircle(0, 0, 8 / cs_viewScale, "#fec11e", "#ffffff", 2)
            cs_drawRoot.graphics.drawLine(0, 0, cs_pointImgs[i].x, cs_pointImgs[i].y, "#fec11e", 2)
        }
        else {
            cs_drawRoot.graphics.drawLine(cs_pointImgs[i - 1].x, cs_pointImgs[i - 1].y, cs_pointImgs[i].x, cs_pointImgs[i].y, "#fec11e", 2);
        }
    }
    // 闭合
    if (points.length >= 2) {
        cs_drawRoot.graphics.drawLine(cs_pointImgs[points.length - 1].x, cs_pointImgs[points.length - 1].y, 0, 0, "#fec11e", 2);
        updateRangeArea_customShapeSystem();
    }
    // 
    if (cs_currentPointIndex != -1 && cs_pointImgs[cs_currentPointIndex]) pointType(cs_pointImgs[cs_currentPointIndex], 1);
    cs_addImg.pos(cs_pointImgs[points.length - 1].x, cs_pointImgs[points.length - 1].y + (15 + (cs_addImg.pivotY)) / cs_viewScale);
    if (points.length == cs_pointImgs.length) return;
    for (let i = cs_pointImgs.length - 1; i > points.length - 1; i--) {
        removeMovePointImg(i);
    }
    // 刷新
}
/**
 * 添加事件
 */
function addEvent() {
    stage.on(EventObject.KEY_DOWN, this, keyDown);
    stage.on(EventObject.KEY_UP, this, keyUp);
    stage.on(EventObject.MOUSE_DOWN, this, activaPoint);
    EventUtils.addEventListener(editor, editor.scene.EVENT_SCENE_SCALE_CHANGE, cs_scaleChangeEvent);
    EventUtils.addEventListener(editor, editor.scene.EVENT_SAVE_AS_MODEL_PRESET_START, cs_savePresetStart);
    EventUtils.addEventListener(editor, editor.scene.EVENT_SAVE_AS_MODEL_PRESET_END, cs_savePresetEnd);
}
/**
 * 清理事件
 */
function clearEvent() {
    stage.off(EventObject.KEY_DOWN, this, keyDown);
    stage.off(EventObject.KEY_UP, this, keyUp);
    stage.off(EventObject.MOUSE_DOWN, this, activaPoint);
    owner.lockKeyMove = owner.lockDelete = false;
    EventUtils.removeEventListener(editor, editor.scene.EVENT_SCENE_SCALE_CHANGE, cs_scaleChangeEvent);
    EventUtils.removeEventListener(editor, editor.scene.EVENT_SAVE_AS_MODEL_PRESET_START, cs_savePresetStart);
    EventUtils.removeEventListener(editor, editor.scene.EVENT_SAVE_AS_MODEL_PRESET_END, cs_savePresetEnd);
}
/**
 * 激活点
 */
function activaPoint() {
    cs_currentPointIndex = -1;
    for (let i = 0; i < cs_pointImgs.length; i++) {
        if (cs_pointImgs[i].hitTestPoint(stage.mouseX, stage.mouseY)) {
            cs_currentPointIndex = i;
            pointType(cs_pointImgs[i], 1);
        } else pointType(cs_pointImgs[i], 0);
    }
    owner.lockKeyMove = owner.lockDelete = cs_currentPointIndex != -1;
}
/**
 * 点状态
 */
function pointType(pointImg: UIBitmap, type: number) {
    if (type) {
        pointImg.graphics.drawRect(0, 0, 18, 18, "#5990ff", "#ffffff", 1.5);
    } else pointImg.graphics.drawRect(0, 0, 18, 18, "#fec11e", "#ffffff", 1.5);
    pointImg.graphics.drawRect(6, 6, 6, 6, "#ffffff");
}
/**
 * 移除轨迹点图片
 * @param index 索引，默认-1，即清除所有
 */
function removeMovePointImg(index: number = -1) {
    if (index == -1) {
        //移除所有
        cs_pointImgs.forEach(v => {
            v.removeSelf();
            v.dispose();
        })
        cs_pointImgs = [];
        if (cs_addImg) cs_addImg.removeSelf();
    } else {
        cs_pointImgs[index].removeSelf();
        cs_pointImgs[index].dispose();
        cs_pointImgs.splice(index, 1);
    }
}
/**
 * 鼠标悬浮
 */
function onPointMouseOver(index: number) {
    if (cs_pointImgs[index]) pointType(cs_pointImgs[index], 1);
}
/**
 * 鼠标移出
 */
function onPointMouseOut(index: number) {
    if (cs_pointImgs[index] && cs_currentPointIndex != index) pointType(cs_pointImgs[index], 0);
}
/**
 * 点击轨迹点
 */
function onPointMouseDown(index: number) {
    owner.lockDrag = true;
    let pointImg = cs_pointImgs[index];
    if (!pointImg) return;
    pointImg.startDrag();
}

/**
 * 鼠标抬起
 */
function onPointMouseUp() {
    owner.lockDrag = false;
}

/**
 * 点击加号
 */
function onAddClick() {
    let points = soModule.pointArr;
    points.push(newPoint());
    editor.scene.recordHistory();
    updateComp("pointArr", points);
    showMovePointImg();
}

/**
 * 新增点位置
 */
function newPoint(index: number = 0): { x: number, y: number } {
    let points = soModule.pointArr;
    let pos = points.length == 0 + index ? (cs_isRelativePoint ? { x: 0, y: 0 } : { x: owner.x, y: owner.y }) : { x: points[points.length - 1 - index].x, y: points[points.length - 1 - index].y }
    let isRight = (cs_isRelativePoint ? owner.x + pos.x : pos.x) > (owner.scene.width / 2);
    if (isRight) pos.x -= 100;
    else pos.x += 100;
    return pos;
}
/**
 * 拖拽开始
 */
function onPointDragStart() {
    editor.scene.recordHistory();
}
/**
 * 拖拽移动
 */
function onPointDragMove(index: number) {
    let pointImg = cs_pointImgs[index];
    if (!pointImg) return;
    pointImg.x = owner.customHighLayer.mouseX;
    pointImg.y = owner.customHighLayer.mouseY;
    let p2 = index == 0 ? new Point(0, 0) : new Point(cs_pointImgs[index - 1].x, cs_pointImgs[index - 1].y)
    if (cs_shiftKeyDown) {
        if (!cs_lockMoveOri) {
            let lockMoveCheck = new Point(pointImg.x, pointImg.y);
            if (Point.distance(lockMoveCheck, cs_lockMoveSpoint[index]) > 10) {
                cs_lockMoveOri = Math.abs(lockMoveCheck.x - cs_lockMoveSpoint[index].x) > Math.abs(lockMoveCheck.y - cs_lockMoveSpoint[index].y) ? 2 : 1;
            }
        }
    }
    let moveLine = soModule.pointArr;
    pointImg.x = cs_shiftKeyDown && cs_lockMoveOri == 1 ? p2.x : pointImg.x;
    moveLine[index].x = Math.floor(cs_isRelativePoint ? pointImg.x : owner.x + pointImg.x);
    pointImg.y = cs_shiftKeyDown && cs_lockMoveOri == 2 ? p2.y : pointImg.y;
    moveLine[index].y = Math.floor(cs_isRelativePoint ? pointImg.y : owner.y + pointImg.y);
    paleLimit();
    Callback.CallLater(updateComp, this, ["pointArr", moveLine]);
    showMovePointImg();
}

/**
 * 按键按下
 */
function keyDown(e: EventObject) {
    if (e.shiftKey && !cs_shiftKeyDown && soModule.pointArr) {
        cs_shiftKeyDown = true;
        for (let i = 0; i < soModule.pointArr.length; i++) {
            cs_lockMoveSpoint[i] = cs_isRelativePoint ? new Point(soModule.pointArr[i].x, soModule.pointArr[i].y) : new Point(soModule.pointArr[i].x - owner.x, soModule.pointArr[i].y - owner.y);
        }
    } else if (e.keyCode == Keyboard.DELETE && cs_currentPointIndex != -1) {
        //删除节点
        editor.scene.recordHistory();
        soModule.pointArr.splice(cs_currentPointIndex, 1);
        updateComp("pointArr", soModule.pointArr);
        showMovePointImg();
    } else {
        let moveLine = soModule.pointArr;
        let pointImg = cs_pointImgs[cs_currentPointIndex];
        if (cs_currentPointIndex == -1 || !pointImg) return;
        let x = pointImg.x;
        let y = pointImg.y;
        var d = cs_shiftKeyDown ? Config.SCENE_GRID_SIZE : 1;
        switch (e.keyCode) {
            case Keyboard.LEFT:
                x -= d;
                break;
            case Keyboard.RIGHT:
                x += d;
                break;
            case Keyboard.UP:
                y -= d;
                break;
            case Keyboard.DOWN:
                y += d;
                break;
            default:
                return;
        }
        moveLine[cs_currentPointIndex].x = Math.floor(cs_isRelativePoint ? x : owner.x + x);
        moveLine[cs_currentPointIndex].y = Math.floor(cs_isRelativePoint ? y : owner.y + y);
        paleLimit();
        Callback.CallLater(updateComp, this, ["pointArr", moveLine]);
        showMovePointImg();
    }
}
/**
 * 按键抬起
 */
function keyUp(e: EventObject) {
    if (e.keyCode == Keyboard.SHIFT) {
        cs_shiftKeyDown = false;
        cs_lockMoveOri = 0;
        cs_lockMoveSpoint = [];
    }
}
/**
 * 边界限制
 * @return 是否改变
 */
function paleLimit(): boolean {
    let isChange: boolean = false;
    for (let i = 0; i < soModule.pointArr.length; i++) {
        let m = soModule.pointArr[i];
        let minX = cs_isRelativePoint ? -owner.x : 0;
        let minY = cs_isRelativePoint ? -owner.y : 0;
        let maxX = cs_isRelativePoint ? owner.scene.width - owner.x - 1 : owner.scene.width - 1;
        let maxY = cs_isRelativePoint ? owner.scene.height - owner.y - 1 : owner.scene.height - 1;
        let r = { x: m.x, y: m.y }
        m.x = m.x < minX ? minX : (m.x > maxX ? maxX : m.x);
        m.y = m.y < minY ? minY : (m.y > maxY ? maxY : m.y)
        if (r.x != m.x || r.y != m.y) isChange = true;
    }
    return isChange;
}
//------------------------------------------------------------------------------------------------------
//  事件-自定义形状
//------------------------------------------------------------------------------------------------------
function onRemoved_customShapeSystem(): void {
    if (!cs_isInit) return;
    cs_isInit = false;
    removeMovePointImg();
    cs_drawRoot.dispose();
    cs_addImg.removeSelf();
    cs_addImg.dispose();
    cs_currentPointIndex = -1;
    clearEvent();
    cs_lockMoveSpoint = [];
    cs_scaleChangeEvent = null;
    cs_savePresetStart = null;
    cs_savePresetEnd = null;
}
function onChange_customShape(attrName: string): void {
    if (soModule.type != 3) {
        onRemoved_customShapeSystem();
        return;
    }
    if (attrName == "isRelativePoint" || attrName == "pointArr") {
        if (attrName == "pointArr") {
            let isChange = false;
            if (cs_recordLength < soModule.pointArr.length) {
                let length = soModule.pointArr.length;
                if (soModule.pointArr[length - 1].x == 0 && soModule.pointArr[length - 1].y == 0) {
                    let point = newPoint(1);
                    soModule.pointArr[length - 1].x = point.x;
                    soModule.pointArr[length - 1].y = point.y;
                    isChange = true;
                }
            }
            cs_recordLength = soModule.pointArr.length;
            if (paleLimit() || isChange) {
                updateComp("pointArr", soModule.pointArr);
            }
        }
        // if (!owner.dynamic || !owner.autoMove) {
        //     clearEvent();
        //     cs_currentPointIndex = -1;
        // }
        editor.scene.refreshSelectSceneObject();
    } else if (attrName == "dynamic" || attrName == "autoMove") {
        // if (!owner.dynamic || !owner.autoMove) {
        //     removeMovePointImg();
        //     cs_drawRoot.graphics.clear();
        // }
        editor.scene.refreshSelectSceneObject();
    }
    onSelectedOwner_customShape(true);
}
function onSelectedOwner_customShape(isSingleSelect: boolean): void {
    if (soModule.type != 3) return;
    if (!cs_isInit) initMove();
    showMovePointImg();
    cs_recordLength = soModule.pointArr ? soModule.pointArr.length : 0;
    owner.lockDrag = false;
    clearEvent();
    addEvent();
}
function onCancelSelectedOwner_customShape() {
    if (soModule.type != 3 || !cs_isInit) return;
    removeMovePointImg();
    cs_drawRoot.graphics.clear();
    cs_currentPointIndex = null;
    clearEvent();
}
function onOwnerAttributeChange_customShape(attrName: string) {
    if (soModule.type != 3 || !cs_isInit) return;
    if (attrName == "x" || attrName == "y" && editor.scene.currentSceneObject == owner) showMovePointImg();
}
function updateRangeArea_customShapeSystem(): void {
    rangeArea.graphics.clear();
    if (!soModule.showRange) return;
    rangeArea.graphics.drawCircle(0, 0, Math.max(Math.floor(Config.SCENE_GRID_SIZE / 2), 16), soModule.color);
    let points = soModule.pointArr;
