//------------------------------------------------------------------------------------------------------
//  ■ 用于实现场景对象的实时编辑器预览效果
//    -- 该代码为私有域，可任意增加变量，该变量作用域仅供此处使用
//    -- 该代码执行在编辑器中，所以一些游戏运行时独有的函数/类可能无法使用
//    -- 一旦编写了注册相关的逻辑，需要在onRemoved时取消该注册（如setInterval/os.add_ENTERFRAME等）
//       如果该模块已附加在了实体对象上，那么请在编写取消注册的逻辑后再保存或确定，否则可能造成泄漏
//       如果发生不可逆的泄漏，已经影响到了显示和操作，只能在编写正确的代码后通过重启编辑器解决。
//------------------------------------------------------------------------------------------------------
/**
 * 当前模块
 */
declare var soModule: SceneObjectModule_8;
/**
 * 当前模块的拥有者
 */
declare var owner: ProjectClientSceneObject;
/**
 * 当前模块初始化（当添加该模块时）
 */
function init(): void {
    refresh();
}
/**
 * 当移除该模块时
 * 一些自己增加的逻辑如需销毁，必须在此函数中实现
 */
function onRemoved(): void {
    owner.avatar.blendMode = null;
}
/**
 * 当改变当前模块属性时
 * @param attrName 模块的属性名称 
 */
function onChange(attrName: string): void {
    if (attrName == "blendMode") {
        refresh();
    }
}
/**
 * 当前模块每帧执行的函数
 */
function onUpdate(): void {

}
/**
 * 当选中拥有者时的函数
 * @param isSingleSelect 是否单选
 */
function onSelectedOwner(isSingleSelect: boolean): void {
    refresh();
}
/**
 * 当取消选中拥有者时的函数
 */
function onCancelSelectedOwner(): void {

}
/**
 * 当拥有者的一般属性变更时（包含公共模块的属性变更）
 * @param attrName 拥有者的属性名称
 */
function onOwnerAttributeChange(attrName: string): void {

}
/**
 * 当拥有者的模块顺序发生改变时
 * @param fromIndex 模块原位置
 * @param toIndex 模块新位置
 */
function onChangeModulePostion(fromIndex: number, toIndex: number): void {

}

function refresh() {
    switch (soModule.blendMode) {
        case 0:
            owner.avatar.blendMode = null;
            break;
        case 1:
            owner.avatar.blendMode = "lighter";
            break;
        case 2:
            owner.avatar.blendMode = "blend5-1";
            break;
        case 3:
            owner.avatar.blendMode = "blend4-1";
            break;
        case 4:
            owner.avatar.blendMode = "blend4-7";
            break;
        case 5:
            owner.avatar.blendMode = "blend4-4";
            break;
    }
}

